from typing import List

def parse_nested_parens(paren_string: str) -> List[int]:
    """
    Parses a string of nested parentheses and returns a list of maximum depths of nesting at each level.
    
    Args:
    paren_string (str): A string containing nested parentheses.
    
    Returns:
    List[int]: A list where each element represents the maximum depth of nesting at that level.
    """
    groups = paren_string.split()
    result = []
    for group in groups:
        current_depth, max_depth = 0, 0
        # Iterate through each character in the group
        for char in group:
            # If the character is an opening parenthesis, increment the current depth
            if char == '(':
                current_depth += 1
                # Update the maximum depth if the current depth is greater
                max_depth = max(max_depth, current_depth)
            # If the character is a closing parenthesis, decrement the current depth
            elif char == ')':
                current_depth -= 1
        # Append the maximum depth found for this group to the result list
        result.append(max_depth)
    # Return the final list of maximum depths
    return result

# Test cases
assert parse_nested_parens('(()()) ((())) () ((())()())') == [2, 3, 1, 3]
assert parse_nested_parens('') == []
