class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
    def find(self, i):
        if self.parent[i] != i:
            self.parent[i] = self.find(self.parent[i])
        return self.parent[i]
    def union(self, i, j):
        root_i = self.find(i)
        root_j = self.find(j)
        if root_i != root_j:
            self.parent[root_i] = root_j
            return True
        return False

def kruskal_mst(v, edges):
    edges.sort(key=lambda x: x[2])
    uf = UnionFind(v)
    weight_sum = 0
    edges_count = 0
    for s, t, w in edges:
        if uf.union(s, t):
            weight_sum += w
            edges_count += 1
    return weight_sum
