import heapq

def shortest_path(v, edges, r):
    adj = [[] for _ in range(v)]
    for s, t, d in edges:
        adj[s].append((t, d))

    dist = [float('inf')] * v
    dist[r] = 0
    pq = [(0, r)]

    while pq:
        d, u = heapq.heappop(pq)
        if d > dist[u]:
            continue
        for v_node, weight in adj[u]:
            if dist[u] + weight < dist[v_node]:
                dist[v_node] = dist[u] + weight
                heapq.heappush(pq, (dist[v_node], v_node))

    return [-1 if x == float('inf') else x for x in dist]
